<script>
import { Dropdown, DropdownMenu, DropdownItem } from "element-ui";
import getAllBalance from "../utils/getAllBalance";
import BigNumber from "bignumber.js";
import getTronBalance from "../utils/getTronBalance";
import getSolTokenBalance from "../utils/getSolTokenBalance";
import getEOSBalance from "../utils/getEOSBalance";
const ChooseChain = () => import("./common/ChooseChain.vue");

export default {
  name: "WidgetBridgeTrade",
  data() {
    return {
      timer: null, //获取余额计时器
      loadingBalance: false,
      loadingToBalance: false,
      chainType: "from", 
      chainList: [], // 网络列表数据
    };
  },
  computed: {
    info() {
      return this.$store.state.info;
    },
    fromNumber: {
      //兑币数量
      get() {
        return this.$store.state.fromNumber;
      },
      set(val) {
        this.$store.commit("setFromNumber", val);
      },
    },
    toNumber() {
      //收币数量
      return this.$store.state.toNumber - 0;
    },
    bridgeTokens() {
      return this.$store.state.bridgeTokens;
    },
    bridgeFromTokenchain: {
      // from 网络
      get() {
        return this.$store.state.bridgeFromTokenchain;
      },
      set(val) {
        this.$store.commit("setBridgeFromTokenchain", val);
      },
    },
    bridgeToTokenchain: {
      // to网络
      get() {
        return this.$store.state.bridgeToTokenchain;
      },
      set(val) {
        this.$store.commit("setBridgeToTokenchain", val);
      },
    },
    bridgeChooseToken: {
      get() {
        return this.$store.state.bridgeChooseToken;
      },
      set(val) {
        this.$store.commit("setBridgeChooseToken", val);
      },
    },
    bridgeFromNetList() {
      const netList = this.bridgeChooseToken.chains;
      if (this.bridgeToTokenchain) {
        return netList.filter(
          (item) => item.mainNetwork != this.bridgeToTokenchain.mainNetwork
        );
      } else {
        return netList;
      }
    },
    bridgeToNetList() {
      const netList = this.bridgeChooseToken.chains;
      if (this.bridgeFromTokenchain) {
        return netList.filter(
          (item) => item.mainNetwork != this.bridgeFromTokenchain.mainNetwork
        );
      } else {
        return netList;
      }
    },
    connectType() {
      return this.$store.state.wallet.connectType;
    },
    walletAddress() {
      if (this.$store.state.chainId == "000") {
        return this.$store.state.walletPolkadot.addrSS58;
      }
      if (this.$store.state.chainId == "222") {
        return this.$store.state.walletPolkadot.addrSS58CRU;
      } else if (this.$store.state.chainId == "333") {
        return this.$store.state.walletPolkadot.addrSS58DBC;
      } else if (this.$store.state.chainId == "0") {
        return this.$store.state.walletTRON;
      } else {
        return this.$store.state.wallet.address;
      }
    },
  },
  watch: {
    bridgeChooseToken() {
      this.initToken();
      this.init();
    },
    bridgeFromTokenchain() {
      this.bridgeChooseToken.balance = 0;
      this.init();
    },
    bridgeToTokenchain() {
      this.bridgeChooseToken.toBalance = 0;
      this.init();
    },
    bridgeTokens(val) {
      this.initToken();
    },
  },
  components: {
    Dropdown,
    DropdownMenu,
    DropdownItem,
    ChooseChain,
  },
  mounted() {
    this.initToken();
  },
  beforeDestroy() {
    clearInterval(this.timer);
  },
  methods: {
    initToken() {
      if (this.bridgeTokens.length < 1) return;
      const type = this.$route.query.type;
      const amount = this.$route.query.amount;
      const from = this.$route.query.from;
      const to = this.$route.query.to;
      const token = this.$route.query.token;
      if (type == "bridge") {
        //首先判断币
        const tokenList = this.bridgeTokens.filter(
          (item) => item.coin == token
        );
        if (tokenList.length > 0) {
          this.bridgeChooseToken = tokenList[0];
        } else {
          this.bridgeChooseToken = this.bridgeTokens[0];
        }
        console.log(tokenList);
        // 判断from 和to 的链
        const fromList = this.bridgeChooseToken.chains.filter(
          (item) => item.mainNetwork == from
        );
        if (fromList.length > 0) {
          this.bridgeFromTokenchain = fromList[0];
        } else {
          this.bridgeFromTokenchain = this.bridgeChooseToken.chains[0];
        }

        const toList = this.bridgeChooseToken.chains.filter(
          (item) => item.mainNetwork == to
        );

        if (toList.length > 0) {
          this.bridgeToTokenchain = toList[0];
        } else {
          this.bridgeToTokenchain = this.bridgeChooseToken.chains[1];
        }
        //判断 输入的数量
        if (!isNaN(Number(amount))) {
          this.fromNumber = amount;
        }
      } else {
        this.bridgeChooseToken = this.bridgeTokens[0];
        this.bridgeFromTokenchain = this.bridgeChooseToken.chains[0];
        this.bridgeToTokenchain = this.bridgeChooseToken.chains[1];
      }
    },
    openChooseChain(type) {
      this.chainType = type;
      this.$refs.dialog.$refs.ChooseChain.show = true;
      this.chainList =
        type == "from" ? this.bridgeFromNetList : this.bridgeToNetList;
    },
    init() {
      clearInterval(this.timer);
      this.getTokenBalance();
      this.getToTokenBalance();
      const self = this;
      this.timer = setInterval(() => {
        self.getTokenBalance();
        self.getToTokenBalance();
      }, 5000);
    },
    handleCommand(command) {
      this.bridgeChooseToken = command;
    },
    fromHandleCommand(command) {
      this.bridgeFromTokenchain = command;
    },
    toHandleCommand(command) {
      this.bridgeToTokenchain = command;
    },
    exchangeTokens() {
      const from = this.bridgeFromTokenchain;
      const to = this.bridgeToTokenchain;
      this.bridgeFromTokenchain = to;
      this.bridgeToTokenchain = from;
    },
    async getTokenBalance() {
      //获取代币余额
      if (this.loadingBalance) return;
      if (this.connectType == "") {
        return;
      }
      //trx获取余额
      if (
        this.bridgeFromTokenchain.mainNetwork === "TRX" &&
        this.connectType == "TronLink"
      ) {
        this.loadingBalance = true;
        if (this.connectType != "TronLink") return;
        const reslt = await getTronBalance();
        let tronWeb = window.tronWeb;
        if (this.bridgeChooseToken.coin === "TRX") {
          this.bridgeChooseToken.balance = tronWeb
            .fromSun(reslt.balance)
            .toString();
        } else {
          if (this.bridgeFromTokenchain.contact.length > 10) {
            const contract = await tronWeb
              .contract()
              .at(this.bridgeFromTokenchain.contact);
            const trc20 = await contract
              .balanceOf(this.$store.state.walletTRON)
              .call();
            const bal = tronWeb
              .toBigNumber(trc20)
              .shiftedBy(
                -(this.bridgeFromTokenchain.coinDecimal != null
                  ? this.bridgeFromTokenchain.coinDecimal
                  : 18)
              )
              .toFixed(6, BigNumber.ROUND_DOWN);
            this.bridgeChooseToken.balance = bal > 0 ? bal : 0;
          } else {
            let assets = reslt.assetV2;
            if (assets && assets.length !== 0) {
